link.script:
window.dataLayer = window.dataLayer || [];
function gtag(){dataLayer.push(arguments);}
gtag('js', new Date());

gtag('config', 'UA-110388181-1');

$(function() {
	$.schoolListMinibase({
		'miniBaseDomainID': 4,
		'miniBaseModuleInstanceID': 2794,
		'cache': true,
		'schoolListReady': function(schoolList) {
			/*
			The returned value is an array of objects and each object is a school record.
			The fields are: schoolList.schoolName, schoolList.schoolType, schoolList.schoolUrl, schoolList.schoolAlias, schoolList.schoolVisible
			*/

			var schools = '';

			for(var i in schoolList) {
				if(schoolList[i].schoolVisible.toLowerCase() == 'true') {
					schools += '
' + schoolList[i].schoolName + '';
				}
			}

			$('.sw-mystart-dropdown.schoollist .sw-dropdown-list').html(schools);
		}
	});
});

    $(function() {
    	CreativeTemplate.Init();
    });

	var CreativeTemplate = {
    	// PROPERTIES
        "KeyCodes": { "tab": 9, "enter": 13, "esc": 27, "space": 32, "end": 35, "home": 36, "left": 37, "up": 38, "right": 39, "down": 40 },
        "IsMyViewPage": false, // UPDATES IN SetTemplateProps METHOD
        "ShowDistrictHome": true,
        "ShowSchoolList": false, // UPDATES IN SetTemplateProps METHOD
        "ShowTranslate": true,
		"ShowFullSiteButton": true,
        "ChannelBarIsSticky": false,
        "ScrollButtonIsSticky": false,
        "SlideshowDescFixed": false,

		// METHODS
        "Init": function() {
            // FOR SCOPE
            var _this = this;

            this.SetTemplateProps();
            this.RsMenu();
            this.JsMediaQueries();
			this.Translate();
            this.SchoolList();
            this.MyStart();
            this.Header();
            this.Search();
            this.ChannelBar();
            this.SetChannelDropdownPositions();
            this.StickyChannelBar();
            this.Slideshow();
            this.CheckSlideshow();
            this.StreamingVideo();
            this.StickyScrollBtn();
            this.Body();
            this.Shortcuts();
            this.ModEvents();
            this.GlobalIcons();
            this.AppAccordion();
            this.Footer();
            this.ArticleSliders();
            this.SocialIcons();
            this.ViewFullSite();

            $(window).load(function() { _this.WindowLoad(); });
            $(window).resize(function() { _this.WindowResize(); });
            $(window).scroll(function() { _this.WindowScroll(); });

            csGlobalJs.OpenInNewWindowWarning();
        },

