                    "target": "_self"
                },
                {
                	"show": true,
                    "text": "Skyward",
                    "url": "https://skyweb1.jsmorton.org/scripts/wsisa.dll/WService=wsEAplus/seplog01.w",
                    "target": "_self"
                },
