        window.resizeMiniCalendar20200429075908247 = function () {
        var calId = 'miniCal20200429075908247';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200429075908247';
        var calMonthFullId = 'miniCalMonthFull20200429075908247';
        var calMonthShortId = 'miniCalMonthShort20200429075908247';
        var calGoToCalFullId = 'miniCalGotToCalFull20200429075908247';
        var calGoToCalShortId = 'miniCalGoToCalShort20200429075908247';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

