link.script:
$(document).ready(function() {
    // Sticky menu at top of page
    var stickyOffset = $('.menu_bar').offset().top;
	$(window).scroll(function(){
		var sticky = $('.menu_bar'),
			scroll = $(window).scrollTop();
		sticky.toggleClass('fixed', scroll >= stickyOffset);
	});
});

body.script:
(function(d, s, id) {
  var js, fjs = d.getElementsByTagName(s)[0];
  if (d.getElementById(id)) return;
  js = d.createElement(s); js.id = id;
  js.src = 'https://connect.facebook.net/en_US/sdk.js#xfbml=1&version=v2.11&appId=394537480629698';
  fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));

div.script:
$(document).ready(function(){
	$('.owl-carousel').owlCarousel({
	  	loop:true,
        autoplay:true,
        autoplayTimeout:8000,
        autoplayHoverPause:true,
		margin:28,
		nav:true,
		responsive:{
			0:{
				items:1
			},
			700:{
				items:2
			},
			1024:{
				items:3
			},
			1500:{
				items:4
			}
		}
	});
    $('.home_page_news').dotdotdot({
        height: 380
    });
    $('.home_page_news').click(function() {
        window.location.href  = $(this).attr('data-url');
    });
    $('#pe').change(function() {
        var url = 'index.php?elem_pe=' + $(this).val();
        window.location.href = url;
    });
	$('#calendar').fullCalendar({
		defaultView: 'listDay',
		events: 'https://www.district106.net/d106/modules/calendar/event_ajax.php?uid=',
		header: {left:'prev',center:'title',right:'next'},
		eventRender: function(event, el) {
			el.qtip({
			  style: { classes: 'qtip-light' },
			  content: event.description,
			  position: {
				  adjust: {
					  x:-250
				  }
			  },
			});
		},
		contentHeight: 436
	});
});
