})(jQuery);

;(function($) {
  var $submit = $('#vlistSubmit');
  $('#news-update input:radio').on('click', function() {
    if ($(this).val() == 'S') {
      $submit.text('Subscribe');
    } else {
      $submit.text('Unsubscribe');
    }
  });

  $('#news-update').on('shown', function () {
    $('#news-update input:text:first').focus();
  });

  $(function() {
    // If coming from the link in vnews/vlist_thankyou.tpl, show the modal form.
    if (window.location.href.indexOf('?show-news-update=1') !== -1) $('#news-update').modal('show');

    // If "#news-update-signup" is in the URL - trigger the modal dialog to show
    var target = window.location.hash.replace("#", "");
    if (target.length && target == "news-update-signup") $('#news-update').modal('show');
  });
})(jQuery);

