function resize_sp1883() {
	var windowWidth = Math.max(parseFloat("1500"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("1000");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel1883Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel1883Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel1883Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel1883Div").style.height = Math.max((parseFloat("472")/parseFloat("1500") * windowWidth), parseFloat("472")) + "px";
	document.getElementById("carousel1883Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel1883Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

if(window.addEventListener) {
	window.addEventListener("resize", function() { resize_sp1883(); }, false);
} else if(window.attachEvent) {
	window.attachEvent("onresize", function() { resize_sp1883(); });
}
resize_sp1883();

