if(!window.resizeMiniCalendar20200429080525329) {
        window.resizeMiniCalendar20200429080525329 = function () {
        var calId = 'miniCal20200429080525329';
        var width = $('#' + calId).width();
        var calYearId = 'miniCalYear20200429080525329';
        var calMonthFullId = 'miniCalMonthFull20200429080525329';
        var calMonthShortId = 'miniCalMonthShort20200429080525329';
        var calGoToCalFullId = 'miniCalGotToCalFull20200429080525329';
        var calGoToCalShortId = 'miniCalGoToCalShort20200429080525329';
        if(width < 250) {
            $('#' + calYearId).hide();
            $('#' + calGoToCalFullId).hide();
            $('#' + calGoToCalShortId).show();
        }
        else {
            $('#' + calYearId).show();
            $('#' + calGoToCalFullId).show();
            $('#' + calGoToCalShortId).hide();
        }
        if(width < 170) {
                $('#' + calMonthFullId).hide();
                $('#' + calMonthShortId).show();                                       
        }
        else {
                $('#' + calMonthFullId).show();
                $('#' + calMonthShortId).hide();                                       
        }
    }               
}

