});

function UpdateUserControl(elem) {
    if (elem) {
        var userCtrl = document.getElementById(elem.getAttribute("userctrlid"));
        if (userCtrl) {
            userCtrl.style.display = elem.checked ? "" : "none";
        }
    }
}

