slides = document.getElementsByClassName('slide');
dots = document.getElementsByClassName('dot');
len = slides.length;
slideIndex = len - 1;
function showSlide(n)
{	clearTimeout(timeout);
slideIndex = n;
for(var i=0; i < len; i++) slides[i].style.display = (i == slideIndex ? 'block' : 'none');
for(var j=0; j < dots.length; j++) dots[j].className = (j == slideIndex ? 'dot active' : 'dot');
if(len > 1) timeout = setTimeout(nextSlide, 10000);
}
function nextSlide(n) {showSlide((len + slideIndex + (n || 1)) % len); return false;}
function gotoSlide(n) {showSlide((len + (n || 1) - 1) % len); return false;}
gotoSlide();

body.script:
function googleTranslateElementInit()
{	new google.translate.TranslateElement(
{	pageLanguage: 'en',
layout: google.translate.TranslateElement.InlineLayout.SIMPLE
}, 'google_translate_element');
// begin accessibility compliance
[].forEach.call(document.querySelectorAll('img.goog-te-gadget-icon'), function(e){e.alt = 'Google Translate';});
[].forEach.call(document.querySelectorAll('div#goog-gt-tt div.logo img'), function(e){e.alt = 'translate';});
[].forEach.call(document.querySelectorAll('div#goog-gt-tt .original-text'), function(e){e.style.textAlign = 'left';});
[].forEach.call(document.querySelectorAll('.goog-te-gadget-simple .goog-te-menu-value span'), function(e){e.setAttribute('role', 'menuitem');});
[].forEach.call(document.querySelectorAll('.goog-te-menu-frame'), function(e){e.title = 'Google Translate Language Options';});
// end accessibility compliance
}

(function(d, s, id) {
var js, fjs = d.getElementsByTagName(s)[0];
if (d.getElementById(id)) return;
js = d.createElement(s); js.id = id;
js.src = 'https://connect.facebook.net/en_US/sdk.js#xfbml=1&version=v3.2&appId=247010148744687&autoLogAppEvents=1';
fjs.parentNode.insertBefore(js, fjs);
}(document, 'script', 'facebook-jssdk'));
