            $(".hp-content.one .ui-widget.app.headlines, .hp-content.four .ui-widget.app.headlines").each(function() {
            	var backgroundColor = ($(this).closest(".hp-content").hasClass("one")) ? "rgba(" + _this.HexToRgb("#15171C") + ", " + regionBHeadlineOpacity + ")" : "rgba(" + _this.HexToRgb("#8D1D20") + ", " + regionEHeadlineOpacity + ")";

                $(".ui-articles li", this).each(function() {
