        }
    }
    , sendBeat: (function () {
        var beatUrl = 'https://frog.wix.com/bt?src=29&evid=3'
            + '&v=1.5739.0'
            + '&msid=3b96a57d-8b9b-4fab-8991-f9588939fd14'
            + '&isp=1'
            + '&st=2'
            + '&dc=42'
            + '&iss=1'
