        function Popup() {
            var self = this;

            self.init = function () {
                self.closePopupBehavior();
                self.outsideClickBehavior();
            };
    
            // function to show our popups
            self.showPopup = function(whichpopup){
                var docHeight = $(document).height(); //grab the height of the page
                var scrollTop = $(window).scrollTop(); //grab the px value from the top of the page to where you're scrolling
                $('.overlay-bg').show().css({'height' : docHeight}); //display your popup background and set height to the page height
                $('.popup'+whichpopup).show().css({'top': scrollTop+20+'px'}); //show the appropriate popup and set the content 20px from the window top
            };
    
            // function to close our popups
            self.closePopup = function (){
                $('.overlay-bg, .overlay-content, .overlay-content-coach').hide(); //hide the overlay
            };

            // hide popup when user clicks on close button or if user clicks anywhere outside the container
            self.closePopupBehavior = function () {
                $('.close-btn, .overlay-bg').click(function(){
                    self.closePopup();
                });  
            };

            // hide the popup when user presses the esc key
            self.outsideClickBehavior = function () {
                $(document).keyup(function(e) {
                    if (e.keyCode == 27) { // if user presses esc key
                        self.closePopup();
                    }
                });  
            };
        }

