	$('.nav-main > ul > li > a').focus(function () {
	    $('.nav-main > ul > li').removeClass('show-menu');
	    $(this).parent().addClass('show-menu');
	});
	
	// Clear the menu class if clicked anywhere in the document.
	$(document).click(function () {
	    $('.nav-main > ul > li').removeClass('show-menu');
