	function dipNavigationDelay_474611(nums,speed){
			
			var slideshowMotionSpeed = slideshowMotionSpeed_474611();
			if(speed == '' || speed == null){
				if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
					speed = 800;
				}else{
					speed = slideshowMotionSpeed;
				}
			}
			
			if(dipNavTimeVar_474611 == 1){
				dipNavigation_474611(nums,speed);
			}else{
				if(typeof dipNavTime_474611 !== "undefined"){
					clearTimeout(dipNavTime_474611);
				}
				
				dipNavTime_474611 = setTimeout(function(){dipNavigationDelay_474611(nums,speed)},speed);
			}
			timeClear = 1;
	}
	
	function dipNavigation_474611(nums,speed){
		clearTimeout(slideshowTimer_474611);
		
		/*if(typeof slideshowJump_474611 !== "undefined"){
			clearTimeout(slideshowJump_474611);
		}*/
		dipNavTimeVar_474611 = 0;
		var slideshowMotion = slideshowMotion_474611();
		var slideshowMotionSpeed = slideshowMotionSpeed_474611();
		var slideshowQuickNavSpeed = slideshowQuickNavSpeed_474611();
		var complete = document.getElementById('complete_474611').value;
		var currentNum = document.getElementById('currentNum_474611').value;
		var time = document.getElementById('slideshowSpeed_474611').value;
		var totalDips = document.getElementById('totalDips_474611').value;
		var direction = directions_474611(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		var directionSkip = 0;
		
