			if(slideshowMotionSpeed == '' || slideshowMotionSpeed == null){
				speed = 800;
			}else{
				speed = slideshowMotionSpeed;
			}
		}
		
		if(currentNum != nums && complete == 1){
		
			if(nums == 'p'){nums = parseInt(currentNum) - 1;}
			if(nums == 'n'){nums = parseInt(currentNum) + 1;}
			nums = parseInt(nums);
			
			if(slideshowMotion != 'f'){
				if(currentNum < nums){directionOn = direction.directionOn;directionOff = direction.directionOff;
				}else{directionOn = direction.directionOff;directionOff = direction.directionOn;}
				//if(currentNum < parseInt(nums) - 1){nums = parseInt(currentNum) + 1;}
				//if(currentNum > parseInt(nums) + 1){nums = parseInt(currentNum) - 1;}
			}
			
			if(nums < 1){nums = totalDips;}
			if(nums > totalDips){nums = 1;}
			
			/*if(!currentNum < parseInt(nums) - 1 && !currentNum > parseInt(nums) + 1){
					newNum = nums;
				}*/
			
			if($('#nav_474611' + currentNum).length){
				$('#nav_474611' + currentNum).removeClass('on');
			}
			if($('#nav_li_474611' + currentNum).length){
				$('#nav_li_474611' + currentNum).removeClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_474611' + currentNum).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_474611' + currentNum).fadeOut(speed);
			}

			if($('#videoShell_474611' + currentNum).length){
					closePlayer_474611(currentNum);
					$('#videoShell_474611' + currentNum).fadeOut(speed);
				}
			if($('#dipContent_474611' + currentNum).length){
				$('#dipContent_474611' + currentNum).fadeOut(speed);
			}
			
			if($('#nav_474611' + currentNum).length){
				$('#nav_474611' + (nums)).addClass('on');
			}
			if($('#nav_li_474611' + currentNum).length){
				$('#nav_li_474611' + (nums)).addClass('on');
			}
			if(slideshowMotion != 'f'){
				$('#dipImage_474611' + (nums)).show("slide", { direction: directionOn }, speed, function(){
					document.getElementById('complete_474611').value = 1;
					dipNavTimeVar_474611 = 1;
				});
			}else{
				$('#dipImage_474611' + (nums)).fadeIn(speed);
				dipNavTimeVar_474611 = 1;
			}
			if($('#videoShell_474611' + nums).length){
					$('#videoShell_474611' + nums).fadeIn(speed);
				}
			if($('#dipContent_474611' + nums).length){
				$('#dipContent_474611' + (nums)).fadeIn(speed);
			}
			
			document.getElementById('currentNum_474611').value = nums;
			//slideshowTimer=setTimeout("switchDip_474611(" + nums + ")",time);
			//currentNum = document.getElementById('currentNum_474611').value;
			
			/*if(slideshowMotion != 'f'){
				if(currentNum < (nums - 1) && currentNum != totalDips){
					slideshowJump_474611=setTimeout("dipNavigation_474611(" + (nums + 1) + "," + slideshowQuickNavSpeed + ")",speed);
