				}
			}*/
			//dipNavTimeVar_474611 = 1;
		}
	}
	
	function switchDip_474611(num,pause){
		clearTimeout(slideshowTimer_474611);
		var timer;
		var nums = num;
		var slideshowMotion = slideshowMotion_474611();
		var speed = slideshowMotionSpeed_474611();
		var currentNum = document.getElementById('currentNum_474611').value;
		var totalDips = document.getElementById('totalDips_474611').value;
		var video = document.getElementById('video_474611').value;
		if(speed == '' || speed == null){
			speed = 800;
		}
		
		var direction = directions_474611(slideshowMotion);
		var directionOn = direction.directionOn;
		var directionOff = direction.directionOff;
		
		if(totalDips > 1 && video == 0){
		
			if(nums < 1){nums = 1;}
			if(nums > totalDips){nums = totalDips;}
			
			if($('#nav_474611' + nums).length){
				$('#nav_474611' + nums).removeClass('on');
			}
			
			if($('#nav_li_474611' + nums).length){
				$('#nav_li_474611' + nums).removeClass('on');
			}
			
			if(slideshowMotion != 'f'){
				if($('#videoShell_474611' + nums).length){
					$('#videoShell_474611' + nums).hide();
				}
				$('#dipImage_474611' + nums).hide("slide", { direction: directionOff }, speed);
			}else{
				$('#dipImage_474611' + nums).fadeOut(speed);
			}
			
			if($('#videoShell_474611' + nums).length){
				$('#videoShell_474611' + nums).fadeOut(speed);
			}
			if($('#dipContent_474611' + nums).length){
				$('#dipContent_474611' + nums).fadeOut(speed);
			}
			
			if(nums >= totalDips){nums = 1;}else{nums = nums + 1;}
			
			if($('#nav_474611' + nums).length){
				$('#nav_474611' + nums).addClass('on');
			}
			
			if($('#nav_li_474611' + nums).length){
				$('#nav_li_474611' + nums).addClass('on');
			}
			
			if(slideshowMotion != 'f'){
				$('#dipImage_474611' + nums).show("slide", { direction: directionOn }, speed, function(){
				if($('#videoShell_474611' + nums).length){
					$('#videoShell_474611' + nums).fadeIn(speed);
					}	
				});
			}else{
				$('#dipImage_474611' + (nums)).fadeIn(speed);
			}
			
			if(slideshowMotion == 'f'){
				if($('#videoShell_474611' + nums).length){
					$('#videoShell_474611' + nums).fadeIn(speed);
				}
			}
			if($('#dipContent_474611' + nums).length){
				$('#dipContent_474611' + (nums)).fadeIn(speed);
			}
				
			if(num < totalDips){timer = num + 1;}else{timer = 1;}
			rotateDip_474611(timer);
			document.getElementById('currentNum_474611').value = nums;
		}
	}
	
	function slideshowMotion_474611(){
		if($('#slideshowMotion_474611').length){
			slideshowMotion = document.getElementById('slideshowMotion_474611').value;
		}else{
			slideshowMotion = 'f';
		}
		if(slideshowMotion == ''){slideshowMotion = 'f'}
		return slideshowMotion;
	}
	
	function slideshowMotionSpeed_474611(){
		if($('#slideshowMotionSpeed_474611').length){
			slideshowMotionSpeed = document.getElementById('slideshowMotionSpeed_474611').value;
		}else{
			slideshowMotionSpeed = '';
		}
		return slideshowMotionSpeed;
	}
	
	
	function slideshowQuickNavSpeed_474611(){
		if($('#slideshowQuickNavSpeed_474611').length){
			slideshowQuickNavSpeed = document.getElementById('slideshowQuickNavSpeed_474611').value;
		}else{
			slideshowQuickNavSpeed = 50;
		}
	
		return slideshowQuickNavSpeed;
	}
	
	//motion direction
	function directions_474611(slideshowMotion){
		switch(slideshowMotion){
			case 'sr':
			directionOn = 'left';
			directionOff = 'right';
			break;
			case 'su':
			directionOn = 'down';
			directionOff = 'up';
			break;
			case 'sd':
			directionOn = 'up';
			directionOff = 'down';
			break;
			default:
			directionOn = 'right';
			directionOff = 'left';
		}
		
		return {directionOn: directionOn, directionOff: directionOff};
	}

	//video
	function playvideo_474611(num){
		getVideoiframeSrc_474611(num);
		$('#videoPlayerClose_474611' + num).html('Close X');
		$('#videoPlayer_474611' + num).fadeIn(800);
		$('#video_474611').val(1);
		videoVloseTimeOut_474611 = setTimeout("$('#videoPlayerClose_474611" + num + "').html('X')",2500);
	}
	
	function closePlayer_474611(num){
		$('#videoPlayer_474611' + num).fadeOut(200);
		$('#video_474611').val(0);
		var video = $('#VideoPlayback_474611' + num).attr("src");
		$('#VideoPlayback_474611' + num).attr("src","");
		$('#VideoPlayback_474611' + num).attr("src",video);
		reRunSlideshow_474611();
	}
	
	function getVideoiframeSrc_474611(num){
		var iframeSrc;
		if($('.Videoiframe_474611' + num).length){
			iframeSrc = $('.Videoiframe_474611' + num).attr('src');
			var n = iframeSrc.indexOf("?");
			if(n != -1){
				$('.Videoiframe_474611' + num).attr('src',iframeSrc + '&wmode=opaque');
			}else{
				$('.Videoiframe_474611' + num).attr('src',iframeSrc + '?wmode=opaque');
			}
			
		}
	}

