$(".group870411").colorbox({rel:'group870411', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group870411").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery870411 = $("a[rel=group870411]").colorbox({maxWidth: "80%"});
$("a#openGallery870411").click(function(e){
    e.preventDefault();
    $gallery870411.eq(0).click();
});

