    });
}
$(document).ready(function () {
    var h = window.innerHeight - 131;
    $(".slideshow_outer_container").css("height", "");
    $(".DownContainer a").css("bottom", "");
    $(".slideshow_caption_container_outer").css("bottom", "");
    var origH = $(".slideshow_outer_container").height();
    if (h < origH) {
        if (h >= 350) {
            $(".slideshow_outer_container").css("height", (h + 1).toString() + "px");
            $(".DownContainer a").css("bottom", (origH - (h + 1) + 50) + "px");
            $(".slideshow_caption_container_outer").css("bottom", (origH - (h + 1) + 20) + "px");
        }
        else {
            $(".slideshow_outer_container").css("height", "351px");
            $(".DownContainer a").css("bottom", (origH - (351) + 50) + "px");
            $(".slideshow_caption_container_outer").css("bottom", (origH - (351) + 20) + "px");
        }
    }
});
