$(document).ready(function(){
    $("header").sticky();
    $("nav").sticky({
        scrollTop: 93,
    });
    if (0 == $(window).scrollTop()) 
    {
        $("header").removeClass("scrolled");
        $("nav").removeClass("scrolled");
        $(".quicklinks").removeClass("scrolled");
    }
    else
    {
        $("header").addClass("scrolled");
        $("nav").addClass("scrolled");
        $(".quicklinks").addClass("scrolled");
    }
    $(window).scroll(function () {
        if (0 == $(window).scrollTop()) 
        {
            $("header").removeClass("scrolled");
            $("nav").removeClass("scrolled");
            $(".quicklinks").removeClass("scrolled");
        }
        else
        {
            $("header").addClass("scrolled");
            $("nav").addClass("scrolled");
            $(".quicklinks").addClass("scrolled");
        }
    });
});

    function resizeCTA() {
        var w = $(window).width();
        if (w < 1272) {
            var newH = ((19 / 72) * w) - 77 / 3;
            if (w <= 991) {
                newH = ((40 / 79) * w) - 1635 / 79;
            }
            $(".CTA").css("height", (newH - 2).toString() + "px");
            $(".div25").css("height", (newH - 2).toString() + "px");
            $(".div100").css("height", ((newH / 2) - 1).toString() + "px");
            $(".CTAStart").css("height", (newH - 2).toString() + "px");
            $(".CTAStart").css("line-height", (newH - 1).toString() + "px");
            $(".div100 .CTAStart").css("height", ((newH / 2) - 1).toString() + "px");
            $(".div100 .CTAStart").css("line-height", ((newH / 2) - 1).toString() + "px");
            $(".TOcontainer").css("height", (newH - 2).toString() + "px");
            $(".div100 .TOcontainer").css("height", ((newH / 2) - 1).toString() + "px");
            $(".TextOverlay").css("height", (newH).toString() + "px");
            $(".div100 .TextOverlay").css("height", ((newH / 2) + 1).toString() + "px");
            if (w <= 560) {
                $(".div25").css("height", (newH * 2).toString() + "px");
                $(".div100").css("height", (newH).toString() + "px");
                $(".div25 .CTAStart").css("height", (newH * 2).toString() + "px");
                $(".div25 .CTAStart").css("line-height", (newH * 2).toString() + "px");
                $(".div100 .CTAStart").css("height", (newH).toString() + "px");
                $(".div100 .CTAStart").css("line-height", (newH).toString() + "px");
                $(".div25 .TOcontainer").css("height", (newH * 2).toString() + "px");
                $(".div100 .TOcontainer").css("height", (newH).toString() + "px");
                $(".div25 .TextOverlay").css("height", ((newH * 2) + 2).toString() + "px");
                $(".div100 .TextOverlay").css("height", ((newH) + 2).toString() + "px");
            }
        }
        else {
            $(".CTA").css("height", "");
            $(".div25").css("height", "");
            $(".div100").css("height", "");
            $(".CTAStart").css("height", "");
            $(".CTAStart").css("line-height", "");
            $(".div100 .CTAStart").css("height", "");
            $(".div100 .CTAStart").css("line-height", "");
            $(".TOcontainer").css("height", "");
            $(".div100 .TOcontainer").css("height", "");
            $(".TextOverlay").css("height", "");
            $(".div100 .TextOverlay").css("height", "");

            $(".div25").css("height", "");
            $(".div100").css("height", "");
            $(".div25 .CTAStart").css("height", "");
            $(".div25 .CTAStart").css("line-height", "");
            $(".div100 .CTAStart").css("height", "");
            $(".div100 .CTAStart").css("line-height", "");
            $(".div25 .TOcontainer").css("height", "");
            $(".div100 .TOcontainer").css("height", "");
            $(".div25 .TextOverlay").css("height", "");
            $(".div100 .TextOverlay").css("height", "");
        }
    }
    $(document).ready(function () {
        resizeCTA();
    });
    $(window).resize(function () {
        resizeCTA();
    });
