function resize_sp697() {
	var windowWidth = Math.max(parseFloat("960"), parseFloat(window.innerWidth || document.documentElement.clientWidth || document.body.clientWidth));
	var contentWidth = parseFloat("");
	if(contentWidth==0) {
		contentWidth = windowWidth;
	}
	document.getElementById("carousel697Div").style.width = windowWidth + "px";
	var infoWrap = document.getElementById("carousel697Div-infowrap");
	if(infoWrap!=null) {
		infoWrap.style.width = windowWidth + "px";
	}
	var transWrap = document.getElementById("carousel697Div-enImgTransWrap");
	if(transWrap!=null) {
		transWrap.style.width = windowWidth + "px";
	}
	// document.getElementById("carousel697Div").style.height = Math.max((parseFloat("468")/parseFloat("960") * windowWidth), parseFloat("468")) + "px";
	document.getElementById("carousel697Div").style.marginLeft = ((contentWidth - windowWidth) / 2.0) + "px";
	var css = document.createElement("style");
	css.type = "text/css";
	var styles = "#carousel697Div-infooverlaycontainer div.carouselInfo { margin-left: " + (-1*((contentWidth - windowWidth) / 2.0)) + "px !important; }";
	if (css.styleSheet) css.styleSheet.cssText = styles;
	else css.appendChild(document.createTextNode(styles));
	document.getElementsByTagName("head")[0].appendChild(css);
	document.body.tabIndex = '0';
	document.body.focus();
}

