	function selectASchool(){
		$(".sw-dropdown-list li:contains('Alternative Site') a").attr("href", "http://careercenter.cpsb.org");


	}	
	function checkForEmptyHeadlines(){
		$("#hp-content .ui-widget.headlines").each(function(){
			if (!$( this ).find(".ui-widget-detail li").length){
				$( this ).hide();
			};

		})
	}
	function pgScreenSize(){
		var getWidth = $(window).width();
		if (getWidth >= 1024) { // 960+
			photoGallery();
			channelBarHeights();
		} if (getWidth >= 768 && getWidth < 1024) { // 768+
			photoGallery();
		} if (getWidth >= 640 && getWidth < 768) { // 640+
			photoGallery();
		} if (getWidth >= 480 && getWidth < 640) { // 480+
			calendarListView();
		} if (getWidth >= 320 && getWidth < 480) { // 320+
			calendarListView();
		}
	}

