	}

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

    function modMystart() {
    	$(".sw-myaccount-chevron").addClass("icon-font").text("d");
		$(".sw-dropdown-arrow").addClass("icon-font").text("d");
    	$("#gb-mystart-left").append($("#sw-mystart-left").children());
		$("#gb-mystart-right").append($("#sw-mystart-right").children());
		$("#gb-mystart-right").prepend($("#sw-mystart-mypasskey"));
        $("#sw-mystart-outer").remove();

        $("#sw-mystart-search").prepend('');
        $("#sw-search-button").attr("role", "button").keydown(function(e) {
        	if(e.keyCode == csKeyCodes.enter || e.keyCode == csKeyCodes.space) {
            	e.preventDefault();
                $(this).click();
            }
        });
    }

	function dynamicContent(){
		// ********** MYSTART ********** //

		// ********** HEADER ********** //
		var lineWrap = '1';
		if(jQuery.trim(lineWrap) == "" || jQuery.trim(lineWrap) == ";0;1;2;3;4"){
			lineWrap = 2;
		}
		var siteName = "Nelson Elementary";
		if(lineWrap == 0){
			$("#gb-sitename").prepend("
" + siteName + "");
		} else {
			siteName = siteName.split(" ");
			var siteNameLength = siteName.length;
			siteNameEnd = jQuery.trim(siteName.splice(-lineWrap, siteName.length).toString().replace(/,/g, " "));
			siteNameBegin = jQuery.trim(siteName.toString().replace(/,/g, " "));
			$("#gb-sitename").prepend("
" + siteNameEnd + "");
			$("#gb-sitename").prepend("
" + siteNameBegin + "");
		}

		// ********** CHANNEL BAR ********** //
		//DOC add first class to first channel item
		$("li.sw-channel-item").first().addClass("first");
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
		//DOC append custom breadcrumb separator
		$("ul.ui-breadcrumbs > li").not($("ul.ui-breadcrumbs > li.ui-breadcrumb-last")).append(" » ");
		//DOC replace more-link text
		$(".headlines a.more-link span").text("More Headlines »");
		$(".announcements a.more-link span").text("More Announcements »");
		//DOC auto-focus username signin field
		$("#swsignin-txt-username").focus();
		//DOC center signed out message and signin button
        if($("div.ui-spn > div > p > span").text() == "You have been signed out."){
            $("div.ui-spn > div").css({"text-align" : "center", "padding" : "50px 0px 50px 0px"});
            //DOC add signed out breadcrumb
            $(".ui-breadcrumbs").append("
Signed Out");
        }
        //DOC add sign in breadcrumb
        if($("#swlogin").length){
            $(".ui-breadcrumbs").append("
Sign In");
        }
        //DOC add first class to first apps
        $(".hp.column").each(function(){
            $(this).find(".ui-widget.app").first().addClass("first");
        });
        var directoryLink = 'https://www.cpsb.org/_directory';
        var gradesLink = 'https://www.cpsb.org/_grades';
        var emailLink = 'https://www.cpsb.org/_email';

        if(jQuery.trim(directoryLink) == ""){
            $(".gb-channel-icon.directory, .hp-global-icon.directory, .sp-global-icon.directory").attr("href", "/domain/200");
        }
        if(jQuery.trim(gradesLink) == ""){
            $(".gb-channel-icon.grades, .hp-global-icon.grades, .sp-global-icon.grades").attr("href", "https://webpams.cpsb.org/jpweb/");
        }
        if(jQuery.trim(emailLink) == ""){
            $(".gb-channel-icon.email, .hp-global-icon.email, .sp-global-icon.email").attr("href", "https://mailhub2.cpsb.org/owa/auth/logon.aspx");
        }
        $(".ui-widget.app.headlines").each(function(){
            $(".ui-article", this).each(function(){
                var headlineURL = $("h1 a", this).attr("href");
                $(".ui-article-description", this).append('Read Full Story');
            });
        });

		// ********** FOOTER ********** //
		//DOC get Schoolwires footer links
		var feedbackURL = $("div#sw-footer-links li:eq(0) a").attr("href");
		var termsURL = $("div#sw-footer-links li:eq(2) a").attr("href");
		var policyURL = $("div#sw-footer-links li:eq(1) a").attr("href");

		$("a#feedback").attr("href", feedbackURL);
		$("a#terms").attr("href", termsURL);
		$("a#policy").attr("href", policyURL);			
	}

	function buildStyleSheet(){
		var primaryColor75 = "rgba(" + hexToRgb('#003366') + ", .75)";
		var quaternaryColor75 = "rgba(" + hexToRgb('#0063af') + ", .75)";

		var dynStyleSheet = document.createElement('style');

		if(dynStyleSheet) {
			dynStyleSheet.setAttribute('type', 'text/css');
			var head = document.getElementsByTagName('head')[0];

			if(head) {
				head.insertBefore(dynStyleSheet, head.childNodes[0]);
			}

			var dynStyles =	'#hp-slideshow .image-button, #hp-slideshow .rs-photo-gallery-control {' + 
					'	background: #003366;' +
					'	background: ' + primaryColor75 + ';' +
					'}' +
					'#hp-slideshow .gallery-desc .caption-text, #hp-slideshow .rs-photo-gallery-desc .rs-photo-caption p {' +
					'	background: #0063af;' +
					'	background: ' + quaternaryColor75 + ';' +
					'}';

			var rules = document.createTextNode(dynStyles);

			if(dynStyleSheet.styleSheet){ // IE
				dynStyleSheet.styleSheet.cssText = dynStyles;
			} else {
				dynStyleSheet.appendChild(rules);
			}
		}
	}

	function hexToRgb(h){
	        var r = parseInt((cutHex(h)).substring(0,2),16);
	        var g = parseInt((cutHex(h)).substring(2,4),16);
	        var b = parseInt((cutHex(h)).substring(4,6),16);
	        return r + ', ' + g + ', ' + b;
	}

	function cutHex(h) {
	        return (h.charAt(0)=="#") ? h.substring(1,7):h;
	}

	function searchText() {
		//DOC custom mystart search placeholder
	        //SEARCH TEXT
	        $("div#sw-mystart-search input#sw-search-input").removeAttr("placeholder");
		$("div#sw-mystart-search input#sw-search-input").attr("value", "Search...");
		$("div#sw-mystart-search input#sw-search-input").focus(function() {
			if($(this).val() == "Search...") {
				$(this).val("");
			}
		});
		$("div#sw-mystart-search input#sw-search-input").blur(function() {
			if($(this).val() == "") {
				$(this).val("Search...");
			}
		});
	}

	function channelBarHeights(){
		var channelBarHeight = $("#gb-channel-list").height();
		$("#gb-channel-list-bg").css("height", channelBarHeight + "px");
		$("#hp-content-outer, #sp-content-outer, #spn-content-outer").css("padding-top", (channelBarHeight - 12) + "px");
		$("#hp-content .gb-page-gradient, #sp-content .gb-page-gradient, #spn-content .gb-page-gradient").css("top", "-" + (channelBarHeight - 12) + "px");
	}

	function calendarListView(){
		//DOC auto click function for schoolwires calendar list view for small mobile devices
		if(!$("a.ui-btn-toggle.list").hasClass("active")){
			$("a.ui-btn-toggle.list").click();
		}
	}

	function modEvents(){
		if (!$(".upcomingevents .joel-month").length) {
			$(".upcomingevents").modEvents({
				columns:"yes"
			});
			eventsByDay(".ui-hp .upcomingevents .ui-articles");
		}
	}

	function eventsByDay(container) {
		var countLi = 0;
		$(".ui-article", container).each(function(){
			if (!$(this).find("h1.ui-article-title.sw-calendar-block-date").size()){
				var moveArticle = $(this).html();
				$(this).parent().prev().children().children().next().append(moveArticle);
				$(this).parent().remove();
			};
		});
	}

	function tabCustomization(){
		if($("#hp-tabs .upcomingevents").length){
			$("#hp-tabs .upcomingevents").removeAttr("style");
		}

		$(".tab-button").first().addClass("first");
		$(".tab-button").last().addClass("last");
	}

