                                        geocodeResult = null;
                                    }
                                    if (geocodeResult != null){
                                        GS_geocodeResults.push(geocodeResult);
                                    }
                                }
                            }
                            if (GS_geocodeResults.length == 0){
                                alert("Location not found. Please enter a valid address.");
                            } else {
                                //alert(formatted_address + " , " + zipcode_or_address);
