div.script:
$(document).ready(function() {

    //remove standard menu
    $('nav > ul > li.mega-menu > ul').remove();
    //attached column divider class
    $('nav > ul > li.mega-menu').each(function() {
        var cls = $( this ).data('columns-class');
        $( this ).find('.mega-menu-column-definer')
        .removeClass (function (index, className) {
            return (className.match (/(^|\s)col-\S+/g) || []).join(' ');
        })
        .addClass(cls);
    });

});


$(function() {
    overrideMinWidth15329();
})
window.addEventListener("orientationchange", function() {
    overrideMinWidth15329();
}, false);
var resizeTimer15329;
$(window).on('resize', function(e) {
    clearTimeout(resizeTimer15329);
    resizeTimer15329 = setTimeout(function() {
        overrideMinWidth15329();
    }, 250);
});
function overrideMinWidth15329() {
    if (!CCM_EDIT_MODE) {
        var parentWidth15329 = $('.info-box-grid-15329').parent().width();
        var boxWidth15329 = $ ('.info-box-grid-15329 .info-box-item').width();
        if (parentWidth15329 < boxWidth15329) {
            $('.info-box-grid-15329 .info-box-item').css('min-width', '90%');
        } else {
            $('.info-box-grid-15329 .info-box-item').css('min-width', '');
            var resizeTimer15329;
            var parentWidth15329 = $('.info-box-grid-15329').parent().width();
            var boxWidth15329 = $ ('.info-box-grid-15329 .info-box-item').width();
            clearTimeout(resizeTimer15329);
            resizeTimer15329 = setTimeout(function() {
                if (parentWidth15329 < boxWidth15329) {
                    $('.info-box-grid-15329 .info-box-item').css('min-width', '90%');
                }
            }, 250);
        }
    }
}


