meta.script:
function OGfindMoreImages(){
	
	var i = $('body').find('img');
	var docHead = ('head');
	var base = 'https://www.akfcs.org';
	
	$(i).each(function(){
		
		var imgSrc = $(this).attr('src');
		if (imgSrc.indexOf("http") >= 0) {
			$imgSrcComplete = imgSrc;
		} else {
			$imgSrcComplete = base+imgSrc;
		}
		
		$(docHead).append('');
	});
	
}
$(document).ready(function() {
	OGfindMoreImages();
});

