$(window).load(function() {

	$('.abl-summary-popup-trigger-1543').magnificPopup({
				type:'inline',
				midClick: true,
				gallery:{enabled:true},
				removalDelay: 300,
				mainClass: 'mfp-fade',
				prependTo: $('.bicycle-ui').add('.ccm-page'),
				showCloseBtn: false,
				callbacks: {
					open: function() {
						$('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					}
				}
	})

	$('#alert-bar-sums-trigger-1543').magnificPopup({
				type:'inline',
				midClick: true,
				removalDelay: 300,
				mainClass: 'mfp-fade',
				prependTo: $('.bicycle-ui').add('.ccm-page'),
				showCloseBtn: false,
				callbacks: {
					open: function() {
						$('#abl-1543').addClass('abl-popup-open')
						$('#abl-1543').find('.abl-popup-close-trigger').removeClass('abl-popup-close-trigger-hidden')
						$('#abl-1543').find('.abl-popup-close-trigger').click(function(evt){
							evt.preventDefault();
							$.magnificPopup.close();
						});
					},
					afterClose: function() {
						$('#abl-1543').removeClass('abl-popup-open mfp-hide').removeAttr('style')
						$('#abl-1543').find('.abl-popup-close-trigger').addClass('abl-popup-close-trigger-hidden')
					}
				}
	})

});

function googleTranslateElementInit() {

	new google.translate.TranslateElement({
		pageLanguage: 'en', 
		layout: google.translate.TranslateElement.InlineLayout.HORIZONTAL, 
		gaTrack: true, 
		gaId: '',
	}, 	'google_translate_element');
	var langSel = $('#google_translate_element').find('.goog-te-combo');
	if ($(langSel).length) {
		var labelEle = 'Google Translate';
		$(langSel).attr('id','goog-te-combo');
		$(labelEle).insertBefore('#goog-te-combo');
	}		
}

