function headerRightSwitch(){	
	var logo = $('header').find('#logo-area');
	var auxArea = $('header').find('#aux-area');
	var logoText = $('header').find('#logo-text-items');
	var auxMenu = $('header').find('#aux-menu');
	$(logo).insertAfter(auxArea);
	$(logoText).insertAfter(auxMenu);
}

$(document).ready(function() {
	
	
		var sliderOuterWrapID = '#slider-outer-wrap-1111';
		var sliderParent = $(sliderOuterWrapID).parent();
		var sliderID = '#slider-1111';
		var sliderLoaderID = '#slider-loader-1111';
		var sliderPagerDotsID = '#slider-pager-dots-1111';
		var sliderPagerDotsLink = $('#slider-pager-dots-1111').find('.slider-pager-dot-link');
		var sliderPagerThumbsID = '#slider-pager-thumbs-1111';
		var sliderPagerThumbsLink = $('#slider-pager-thumbs-1111').find('.slider-pager-thumb-link');
		var sliderControlNextID = '#slider-control-next-1111';
		var sliderControlPrevID = '#slider-control-prev-1111';
		var sliderRevealSpeed = 240;
		
		function revealSlider(slider, loader) {
			$(slider).addClass('active');
			$(loader).fadeOut('slow');
		}
		
		setTimeout(revealSlider(sliderID, sliderLoaderID), sliderRevealSpeed);
		
		$.fn.animateInElements = function() {
			var activeSlide = $(this);
			var captions = $(activeSlide).find('.slider-headings');
			var size = $(captions).outerHeight();
			var headline = $(captions).find('.slider-heading');
			var subhead = $(captions).find('.slider-subheading');
			var button = $(captions).find('.slider-button');
			var overlay = $(activeSlide).find('.slider-overlay');
			var overlayOpacity = $(overlay).attr('data-overlay-opacity');
			var image = $(activeSlide).find('.slider-image');			
			if ($(captions).hasClass('slider-auto-middle-text')) {
				$(captions).css({
					'marginTop':-size/2+'px',
				});		
			}
			$(overlay).addClass('active').css({
				'opacity':overlayOpacity
			});		
			$(image).css({
				'transition-duration': '8000ms'
			});
			$(image).addClass('active');		
			setTimeout(function(){
				$(headline).addClass('active');
			}, 360);	
			setTimeout(function(){
				$(subhead).addClass('active');
			}, 480);	
			setTimeout(function(){
				$(button).addClass('active');
			}, 600);
		};
		
		$.fn.animateOutElements = function() {		
			var activeSlide = $(this);
			var captions = $(activeSlide).find('.slider-headings');
			var size = $(captions).outerHeight();
			var headline = $(captions).find('.slider-heading');
			var subhead = $(captions).find('.slider-subheading');
			var button = $(captions).find('.slider-button');
			var overlay = $(activeSlide).find('.slider-overlay');	
			var image = $(activeSlide).find('.slider-image');	
			$(image).removeClass('active');
			$(overlay).removeClass('active').css({
				'opacity':'0'
			});	
			$(headline).removeClass('active');
			$(subhead).removeClass('active');
			$(button).removeClass('active');
		};
		
		var eqIndex = 1;
				
		var slider = $(sliderID).bxSlider({
			mode: 'horizontal',
			pause: 8000,
			auto: true,
			speed: 500,
			autoHover: false,
			pagerCustom: $(sliderPagerDotsID).add(sliderPagerThumbsID),
			nextText: '',
			prevText: '',
			nextSelector: sliderControlNextID,
			prevSelector: sliderControlPrevID,
			
			//--	After the initial slider load
						onSlideAfter: function (currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
							$(sliderID).find('.active-slide').removeClass('active-slide').animateOutElements();
							$(sliderID + ' > li').eq(currentSlideHtmlObject + eqIndex).addClass('active-slide').animateInElements();
						},
			
			//--	When the slider first loads in, it may have a clone as the first slide
						onSliderLoad: function () {
							$(sliderID + ' > li').eq(eqIndex).addClass('active-slide').animateInElements();
						}

		});
		
		// Stop auto slide when controls are clicked
		$(sliderControlNextID).add(sliderControlPrevID).add(sliderPagerThumbsLink).add(sliderPagerDotsLink).click(function(){
			slider.stopAuto();
		});
				
		// ADA Compliance, Keyboard Nav
		$(document).keydown(function(e){
			// Right arrow 
			if (e.keyCode == 39) {
					slider.goToNextSlide();
					return false;
			// left arrow
			}	else if (e.keyCode == 37) {
					 slider.goToPrevSlide();
					return false;
			}
		});	

		// ADA Compliance, labels for Next/Prev
		$(sliderControlNextID).find('.bx-next').append('Next Slide');
		$(sliderControlPrevID).find('.bx-prev').append('Previous Slide');

		// Make buttons smaller for small screens...
		function sliderButtonSizer(sliderID) {
			
			var btns = $(sliderID).find('.btn');
			
			$(window).on('resize', function () {
					if ($(window).width() < 960) {
						$(btns).addClass('btn-sm');
					} else {
						$(btns).removeClass('btn-sm');
					}	
			}).resize();
			
		}
		sliderButtonSizer(sliderID);

});

// apply button custom classes...
function customButtonClass1434(container){
	var parent = $(container).parent();
	if ($(parent).hasClass('links-btn-primary')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-primary');
	}
	if ($(parent).hasClass('links-btn-success')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-success');
	}
	if ($(parent).hasClass('links-btn-info')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-info');
	}
	if ($(parent).hasClass('links-btn-warning')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-warning');
	}
	if ($(parent).hasClass('links-btn-danger')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-danger');
	}
	if ($(parent).hasClass('links-btn-lg')){
		$(container).find('.btn').addClass('btn-lg');
	}
	if ($(parent).hasClass('links-btn-sm')){
		$(container).find('.btn').addClass('btn-sm');
	}
	if ($(parent).hasClass('links-btn-xs')){
		$(container).find('.btn').addClass('btn-xs');
	}
}
customButtonClass1434('#links-list-1434');

