function isBreakpoint(alias) {
    return $('.device-' + alias).is(':visible');
}

function screenAdjustments() {

    if (!isBreakpoint('xs')) {
    }

    if (isBreakpoint('xs')) {
        $('body').attr('class', 'xs-body');
    }
    if (isBreakpoint('sm')) {
        $('body').attr('class', 'sm-body');
    }
    if (isBreakpoint('md')) {
        $('body').attr('class', 'md-body');
    }
    if (isBreakpoint('lg')) {
        $('body').attr('class', 'lg-body');
    }


}


