function resizeSlider_ir3590() {

	var windowWidth = parseFloat(document.body.offsetWidth);
	var containerWidth = windowWidth;

	var contentWidth = parseFloat("0");
	if(contentWidth==0) {
		contentWidth = containerWidth;
	}

	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("ir3590-ir3590-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("ir3590-ir3590-slider").style.height = Math.max((parseFloat("220")/parseFloat("220") * containerWidth), parseFloat("220")) + "px";
	document.getElementById("ir3590-ir3590-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

