	if(windowWidth<contentWidth) {
		containerWidth = contentWidth;
	}

	document.getElementById("shuffle1-ir941-slider").style.width = containerWidth + "px";
	var fadeSlice = document.getElementById("nivo-slice-0");
	if(fadeSlice!=null) {
		fadeSlice.style.width = containerWidth + "px";
	}
	// document.getElementById("shuffle1-ir941-slider").style.height = Math.max((parseFloat("209")/parseFloat("654") * containerWidth), parseFloat("209")) + "px";
	document.getElementById("shuffle1-ir941-slider").style.marginLeft = ((contentWidth - containerWidth) / 2.0) + "px";

}

img.script:
(function(scripts) {

	for (var i = 0; i < scripts.length; i++) {
		var script = scripts[i];
		if (script.src && script.src.match("wotd2.js")) {
			try {
				var newScript = document.createElement("script");
				var cbName = "cb3784";
				window[cbName] = function(html) {
					newScript.outerHTML = html;
				}
				newScript.src = "/apps/word_of_the_day/hp_word_of_the_day.jsp?cb=" + cbName;
				script.parentNode.replaceChild(newScript, script);
			} catch(e) {
				console.log(e);
			}
			break;
		}
	}

})(document.getElementsByTagName("script"));

link.script:
/*  */

if(window.ActiveXObject) {

	var myLIs = document.getElementById("menu").getElementsByTagName("li");

	for (var i=0; i<myLIs.length; i++) {
		myLIs[i].onmouseover=function() {
			this.className+=" over";
		}

		myLIs[i].onmouseout=function() {
			this.className=this.className.substr(0,this.className.lastIndexOf(" over"));
		}
	}
}

