body.script:
jQuery(function ($) {
	
// 	$('.ds-main-menu').bind('heightChange', function(){
// //         alert('height changed');
//     });
	
	
    $( document ).ready(function() {
		
		
		$(window).scroll(function() {
				if($(window).scrollTop()>=100) {
					if($("header").not(".et-fixed-header")) {
							$("header").addClass("et-fixed-header");
					}
				}
				else {
					$("header").removeClass("et-fixed-header")
				}
		});
		
		

	var registerAPIInterval;
		
    registerAPI();
		
		function registerAPI() {
			
        	clearInterval(registerAPIInterval);
			
        	if(window.fwdu3dcar0) {            
				
				$("#fwdu3dcarDiv0-fluidwidth").hide();
				
				goToNextThumb();
				
            	fwdu3dcar0.addListener(FWDUltimate3DCarousel.THUMB_CHANGE, onThumbChange);
			}
			else {
				registerAPIInterval = setInterval(registerAPI, 100);
			}
		};
		function goToNextThumb() {		
			setTimeout(function() {			
				fwdu3dcar0.gotoThumb(fwdu3dcar0.getCurrentThumbId() + 1);
				$("#fwdu3dcarDiv0-fluidwidth").show();
			}, 6000);        
		}
		function onThumbChange(event) {
			if(checkExist) {
				updateThumb(event.id);
			}
		}
    	function updateThumb(thumbId) {
			thumbId++;
			$("#fwdu3dcarDiv0-fluidwidth div:nth-child(5) >div>div>div").removeClass("current-thumb");
			$("#fwdu3dcarDiv0-fluidwidth div:nth-child(5) >div>div>div:nth-child("+thumbId+")").addClass("current-thumb");
	    }
    });
	
	var checkExist = setInterval(function() {
	   if ($("#fwdu3dcarDiv0-fluidwidth").length) {	   
		  clearInterval(checkExist);
	   }
	}, 100); // check every 100ms

});

