		jQuery('#accordion h2').find('i').addClass('fa-plus-square-o');

		if (jQuery(this).next('.accordion-content').is(':visible')) {
			jQuery('.accordion-content').slideUp();
		} else {
			jQuery(this).next('.accordion-content').slideDown();
			jQuery(this).addClass('is-active');
			jQuery(this).find('i').addClass('fa-minus-square-o');
			jQuery(this).find('i').removeClass('fa-plus-square-o');
		}


	});
