function headerRightSwitch(){	
	var logo = $('header').find('#logo-area');
	var auxArea = $('header').find('#aux-area');
	var logoText = $('header').find('#logo-text-items');
	var auxMenu = $('header').find('#aux-menu');
	$(logo).insertAfter(auxArea);
	$(logoText).insertAfter(auxMenu);
}

$(document).ready(function(){
		
		var bxsliderOuterWrapID = '#bxslider-outer-wrap-886';
		var bxsliderParent = $(bxsliderOuterWrapID).parent();
		var bxsliderID = '#bxslider-886';
		var bxsliderLoaderID = '#bxslider-loader-886';
		var bxsliderPagerDotsID = '#bxslider-pager-dots-886';
		var bxsliderPagerThumbsID = '#bxslider-pager-thumbs-886';
		var bxsliderControlNextID = '#bxslider-next-886';
		var bxsliderControlPrevID = '#bxslider-prev-886';
		var bxsliderRevealSpeed = 240;
		var collageWrap = $(bxsliderOuterWrapID).find('.collage-frames-wrap');
		var collageFrames = $(bxsliderOuterWrapID).find('.collage-frame');
		
		function revealSlider(slider, loader) {
			$(slider).addClass('bxslider-active');
			$(loader).fadeOut('slow');
		}
		
		setTimeout(revealSlider(bxsliderID, bxsliderLoaderID), bxsliderRevealSpeed);
		
		$.fn.fadeInElements = function() {
			var activeSlide = $(this);
			var captions = $(activeSlide).find('.bxslider-captions');
			var size = $(captions).outerHeight();
			var headline = $(captions).find('.bxslider-headline');
			var subhead = $(captions).find('.bxslider-subhead');
			var overlay = $(activeSlide).find('.bxslider-overlay');	
			var collageImage = $(activeSlide).find('.collage-frame');
			$(captions).css({
				'marginTop':-size/2+'px',
			});		
			$(overlay).delay(240).fadeIn('slow');		
			setTimeout(function(){
				$(headline).addClass('bxslider-headline-active');
			}, 360);	
			setTimeout(function(){
				$(subhead).addClass('bxslider-subhead-active');
			}, 480);
						
			
		};
		
		$.fn.fadeOutElements = function() {		
			var activeSlide = $(this);
			var captions = $(activeSlide).find('.bxslider-captions');
			var size = $(captions).outerHeight();
			var headline = $(captions).find('.bxslider-headline');
			var subhead = $(captions).find('.bxslider-subhead');
			var overlay = $(activeSlide).find('.bxslider-overlay');	
			var collageImage = $(activeSlide).find('.collage-frame');
			$(overlay).hide();
			$(headline).removeClass('bxslider-headline-active');
			$(subhead).removeClass('bxslider-subhead-active');
		};
		
		// Mode
					var slideIndex = 0;
			var mode = 'fade';
				
		var slider = $(bxsliderID).bxSlider({
			mode: mode,
			pause: 4000,
			auto: false,
			speed: 500,
			autoHover: false,
			pagerCustom: $(bxsliderPagerDotsID).add(bxsliderPagerThumbsID),
			nextText: '',
			prevText: '',
			nextSelector: bxsliderControlNextID,
			prevSelector: bxsliderControlPrevID,
			adaptiveHeight: true,

			onSlideAfter: function (currentSlideNumber, totalSlideQty, currentSlideHtmlObject) {
				$(bxsliderID).find('.bxslider-item-active').removeClass('bxslider-item-active').fadeOutElements();
				$(bxsliderID + ' > li').eq(currentSlideHtmlObject + slideIndex).addClass('bxslider-item-active').fadeInElements();
			},
			
			onSliderLoad: function () {
				$(bxsliderID + ' > li').eq(slideIndex).addClass('bxslider-item-active').fadeInElements();
			}
			
		});
		
		// Stop auto slide when controls are clicked
		$(bxsliderControlNextID).add(bxsliderControlPrevID).click(function(){
			slider.stopAuto();
		});
				
		// ADA Compliance, Keyboard Nav
		$(document).keydown(function(e){
			// Right arrow 
			if (e.keyCode == 39) {
					slider.goToNextSlide();
					return false;
			// left arrow
			}	else if (e.keyCode == 37) {
					 slider.goToPrevSlide();
					return false;
			}
		});	
		
		// ADA Compliance, labels for Next/Prev
		$(bxsliderControlNextID).find('.bx-next').append('Next Slide');
		$(bxsliderControlPrevID).find('.bx-prev').append('Previous Slide');

		
		// Overflow Popup
		$('.bxslider-popup-trigger').magnificPopup({
			type:'inline',
			midClick: true,
			prependTo: $('.bicycleui-page').add('.ccm-page'),
		});
		
		// Add Borders To Collage Items
		
		function addCollageBorders886(container,collageWrap,collageFrames) {
			var collageParent = $(container).parent();
			
			if($(collageParent).is('[class*="add-borders-"]')) {		
				
				var borderWidth = collageParent.attr('class').match(/\badd-borders-(\d+)\b/)[1]+'px';
				var borderColor = '#'+collageParent.attr('class').slice(-6);
				
				$(container).find(collageFrames).css({
					'boxShadow':'inset 0 0 0 '+ borderWidth +' '+borderColor
				});
			
			}
		}
		addCollageBorders886(bxsliderOuterWrapID,collageWrap,collageFrames);
		

});

// apply button custom classes...
function customButtonClass168(container){
	var parent = $(container).parent();
	if ($(parent).hasClass('links-btn-primary')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-primary');
	}
	if ($(parent).hasClass('links-btn-success')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-success');
	}
	if ($(parent).hasClass('links-btn-info')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-info');
	}
	if ($(parent).hasClass('links-btn-warning')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-warning');
	}
	if ($(parent).hasClass('links-btn-danger')){
		$(container).find('.btn').removeClass('btn-default').addClass('btn-danger');
	}
	if ($(parent).hasClass('links-btn-lg')){
		$(container).find('.btn').addClass('btn-lg');
	}
	if ($(parent).hasClass('links-btn-sm')){
		$(container).find('.btn').addClass('btn-sm');
	}
	if ($(parent).hasClass('links-btn-xs')){
		$(container).find('.btn').addClass('btn-xs');
	}
}
customButtonClass168('#links-list-168');

