    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group866115").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery866115 = $("a[rel=group866115]").colorbox({maxWidth: "80%"});
$("a#openGallery866115").click(function(e){
    e.preventDefault();
    $gallery866115.eq(0).click();
});

