	jQuery(".slider4 a.vgimage").fancybox({
		openEffect  : 'none',
		closeEffect : 'none',
		helpers : {
                    media : {}
		}
	});
        jQuery('.slider4 .slide a.vgimage .thumbnail').each(function(){
            jQuery(this).hover(function(){
                jQuery(this).children('img').transition({ scale: 1.1, duration: 200 });
                jQuery(this).find('.overlay').animate({opacity: 0.6}, 200);
                jQuery(this).find('.overlay-content').animate({opacity: 1}, 200);
            }, function(){
                jQuery(this).children('img').transition({ scale: 1, duration: 200 });
                jQuery(this).find('.overlay').animate({opacity: 0}, 200);
                jQuery(this).find('.overlay-content').animate({opacity: 0}, 200);
            });		
        });
    });

