div.script:
function TranslateFred()
{
    var posisi = document.f_googletranslator.tl.selectedIndex;
    var tl_value = document.f_googletranslator.tl.options[posisi].value;
    document.f_googletranslator.u.value = window.location.href;
    var google_url = 'http://translate.google.com/translate?u=' + document.f_googletranslator.u.value + '&hl=en&ie=UTF-8&sl=auto&tl=' + tl_value;
    window.location.href = google_url;
    return true;
}

html.script:
jQuery("document").ready(function(){
   jQuery(window).scroll(function() {
        if (jQuery(this).scrollTop()>130 && jQuery(window).width()>1259) {
            jQuery('.main-nav-outer-wrapper').attr("style","width: 100%;position: fixed;left:0;right:0;top:0;z-index: 99999;");
            if (jQuery("#megaMenuToggle").css("display") == "block") { 
                jQuery('.input-group-addon-block').attr('style',"width:100%;");
            } else {
                jQuery('.input-group-addon-block').attr('style',"width:15%;");
            }
            jQuery('.form-group').attr('style',"display:block;");
        } 
        else if (jQuery(this).scrollTop()>130 && jQuery(window).width()<=1259) {
            jQuery('.main-nav-outer-wrapper').attr("style","width: 100%;position: fixed;left:0;right:0;top:0;z-index: 99999;");
            jQuery('.form-group').attr('style',"display:block;");
            if (jQuery("#megaMenuToggle").css("display") == "block") { 
                jQuery('.input-group-addon-block').attr('style',"width:100%;");
            } else {
                jQuery('.input-group-addon-block').attr('style',"width:15%;");
            }
        }

        else if (jQuery(this).scrollTop()<=130){
            //console.log(130);
            jQuery('.main-nav-outer-wrapper').removeAttr("style");
            jQuery('.input-group-addon-block').removeAttr("style");
            jQuery('.form-group').removeAttr("style");
        }
        
    });
   jQuery(window).resize(function() {
        /*if (jQuery("#megaMenuToggle").css("display") == "block") { 
            jQuery('.input-group-addon-block').attr('style',"width:100%;");
        } */
        /*if (jQuery(window).width()>1025) { 
            jQuery('.input-group-addon-block').attr('style',"width:25%;");
        } else {
          jQuery('.input-group-addon-block').attr('style',"width:100%;");  
        }*/
        console.log(jQuery(window).width());
        if (jQuery("#megaMenuToggle").css("display") == "block") { 
            jQuery('.input-group-addon-block').attr('style',"width:100%;");
        } 
        if (jQuery(window).width()>1006) { 
            jQuery('.input-group-addon-block').attr('style',"width:25%;");
        }
         if (jQuery(window).width()>1178) {
            jQuery('.input-group-addon-block').attr('style',"width:15%;");
        }
          
   });
});
