$(document).ready(function() {


	/* ---	Equal height function resizeable on window resize ---- */
	if ($(window).width() > 880) {
		delay(function() {
			$('.main .col-item.live-feeds-wrapper, .main .col-item.news-wrapper').css('height', 'auto'); //solve for all you browser stretchers out there!
			setEqualHeight($('.main .col-item.live-feeds-wrapper, .main .col-item.news-wrapper'));
		}, 500);
	}
	$(window).resize(function() {
		if ($(window).width() > 880) {
			delay(function() {
				$('.main .col-item.live-feeds-wrapper, .main .col-item.news-wrapper').css('height', 'auto'); //solve for all you browser stretchers out there!
				setEqualHeight($('.main .col-item.live-feeds-wrapper, .main .col-item.news-wrapper'));
			}, 500);

		} else {
			$(".main .col-item.live-feeds-wrapper, .main .col-item.news-wrapper").css('height', 'auto');
		}
	});

	if (($(window).width() < 880) && ($(window).width() > 480)) {
