div.script:
    $( document ).ready( 
        function ()
        {
            var lSelector = "#content_79576";
            var lRotatorList = $( lSelector + ' .fbcms_imagerotator' );
lRotatorList.on( 
    'cycle-update-view', 
    function(aEvent, aOptions, aSlideOptions, aCurrentSlideElement)
    {
        var lSlide = $(aCurrentSlideElement);
        var lImg = lSlide.find('img');

        var lTitle = lImg.attr('title');
        var lText = lImg.attr('alt');
        var lUrl = null;

        var lUrlElement = lSlide.find('a');
        if (lUrlElement.length == 1)
        {
            lUrl = lUrlElement.attr('href');
        }

        updateHomeFocusOverlay(lTitle, lText, lUrl);
    }
);
lRotatorList.cycle();
        }
    );

