div.script:
$(document).ready(function () {
    $("#d2d129f9-2730-4bd3-937f-2beae0871e6b").owlCarousel({
        navigation: false,
        pagination : false,
        autoPlay: getAutoPlayValue(),
        transitionStyle : "fade",
        items: '10',
        mouseDrag: false,
        singleItem: true});

    function getAutoPlayValue() {
        var autoPlayValue = 3000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 5 > 1 ? autoPlayValue : false;
    }
});

$(document).ready(function () {
    $("#e30e523f-2ee0-4309-bdcf-44a6767a6660").owlCarousel({
        navigation: true, // Show next and prev buttons
        navigationText: [
             "",
             ""
             ],
        items: '10',
        autoPlay : getAutoPlayValue(),

        mouseDrag: false,
        pagination:true,
        paginationNumbers: false,
        responsive:true,
        singleItem: true});

    $('div.promotional-carousel .owl-controls').on('click', function () {
        $('#e30e523f-2ee0-4309-bdcf-44a6767a6660').trigger('owl.stop')
    })

    $('div.promotional-carousel').keyup(function (e) {
        if (e.keyCode == 9 || e.which == 9) {
            $('#e30e523f-2ee0-4309-bdcf-44a6767a6660').trigger('owl.stop');
        }
    });

    // Prevent default on click of owl pagination
    $('.owl-page').on('click', '[href]', function(event){
        event.preventDefault();
    });

    function getAutoPlayValue() {
        var autoPlayValue = 5000;
        if (autoPlayValue == 0) {
            autoPlayValue = false;
        }
        return 6 > 1 ? autoPlayValue : false;
    }

});

jQuery(document).ready(function () {
    jQuery('#0f54d24b-9664-4e98-bd21-e38bb33348e9').hide();
    jQuery('#0f54d24b-9664-4e98-bd21-e38bb33348e9').listSolrResults({
        currentPagePath: '/content/schools/mvca/en',
        label: 'News',
        listFrom: 'solr',
        limit: '3',
        listType: 'news',
        listOrder: 'desc',
        stateAbbr: '',
        viewAllText: 'View All',
        viewAllUrl: '/news.html',
        viewAllTitle: 'View All',
        uuid: '0f54d24b-9664-4e98-bd21-e38bb33348e9',
        isEditMode: 'false',
        paginate: 'false',
        entriesPerPage: '10'
    });
});

