	function appView(){
		//DOC make homepage apps collapsible for small mobile devices
		if(!$("div.ui-widget.app.active").length){
			$("#hp-content div.ui-widget.app:first").addClass("active");
		}
		$("div.ui-hp div.ui-widget-header").click(function(){
			$("div.ui-widget.app").removeClass("active");
			$(this).parent().addClass("active");
		});
	}	

	function useChannelForSection(){
		//DOC use channel name for pagelistnavigation header if one is not present
		if(!$("div.sp.column.one .ui-widget-header h1").length > 0) {
			$("div.sp.column.one .ui-widget-header").append("
");
		}
    }

	function dynamicContent(){
		// ********** MYSTART ********** //
		//DOC fixes search box not allowing spaces
		$("#sw-mystart-search").unbind();
		//DOC replace mystart dropdown arrows
		$(".sw-myaccount-chevron").text("▼");
		$(".sw-dropdown-arrow").text("▼");
		$("#sw-mystart-search").appendTo($("#gb-search"));

		// ********** HEADER ********** //
		var logoCheck = 'true';
		var logoSrc = '/cms/lib/MI02208798/Centricity/Template/GlobalAssets/images///logos/Block V - Orange RGB PNG.png';
		if(logoCheck == "true"){
			$('').load(function(){
				$("#gb-logo a").append($(this));
				$("#gb-logo").show();
			});
		}

		// CHECK TO SEE IF THEY ARE USING A SITENAME AND/OR TAGLINE
		var siteName = "Vassar Public Schools";
		var siteTagline = "Inspire Future Excellence - Learn, Achieve, Succeed";

		var siteNameCheck = 'true';
		if(siteNameCheck == "true") {
			if(siteName != ""){
				$("#gb-sitename #gb-name").show();
			}
		}

		var taglineCheck = 'true';
		if(taglineCheck == "true") {
			if(siteTagline != ""){
				$("#gb-sitename #gb-tagline").show();
			}
		}

		//GLOBAL ICONS
		var iconNum = ' 3';
		if (iconNum >0){
			$("#gb-global-icons").creativeIcons({
			        "iconNum"       : " 3", // NUMBER OF ICONS
			        "icons"         : [
				                ["Athletics/9.png", "Athletics", "http://vassarsports.com/", " _blank"],
				                ["Education Portal/7.png", "Skyward", "http://student.vassar.k12.mi.us", " _blank"],
				                ["Site Utility/2.png", "Calendar", "http://www.arbiterlive.com/School/Calendar/24341#", "_self"],
				                ["Communication/14.png", "Information", "#", "_self"]
				        ],
			        "siteID"        : "8",
			        "siteAlias"     : "entry",
			        "calendarLink"  : "/Page/10",
			        "contactEmail"  : "tnesbitt@vassar.k12.mi.us",
			        "allLoaded"     : function(){
			        	$(".global-icon").first().addClass("first");
					$(".global-icon").last().addClass("last");
			        }
			});
		}
		else{
			$("#gb-global-icons").hide();
		}

		// ********** CHANNEL BAR ********** //
		//DOC add last class to last channel item
		$("li.sw-channel-item").last().addClass("last");

		// ********** BODY ********** //
		//DOC rename first breadcrumb
		$("li.ui-breadcrumb-first > a > span").text("Home");
