div.script:
            $(window).load(function(){
            	var iframe = $('iframe.goog-te-menu-frame').contents();
                iframe.keydown(function(e){
                	if (e.keyCode == 40) { // down arrow get first link
                		e.preventDefault();
	                    e.stopPropagation();
	                    $(this).find('a:first').focus();
                	}
                });
                var menu = iframe.find('a').keydown(function(e){
                	var index = menu.index($(this));
                	if (e.keyCode == 40) { // down arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index + 1).focus();
					}
					if (e.keyCode == 38 && index > 0) { // up arrow
						e.preventDefault();
	                    e.stopPropagation();
						menu.eq(index - 1).focus();
					}
                });

            });

$(function(){load_events_list(0);});
function load_events_list(num){
	$.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/json/2/'+num, function(data) {
		if(data.code==200){
			var htmlist='';
		    for(key in data.response){
			    sdate=new Date(data.response[key].start_date);
			    var style='';
			    if(data.response[key].status){
			    	if(data.response[key].status.indexOf('Cancelled')>-1 || data.response[key].status.indexOf('Rescheduled')>-1 || data.response[key].status.indexOf('Postponed')>-1){
			        	if(data.response[key].status.indexOf('Postponed')>-1){
			            	data.response[key].status=data.response[key].status.substring(0,data.response[key].status.indexOf(' '));
			            }
			            if(data.response[key].status.indexOf('from')==-1) style=';text-decoration:line-through';
			            	data.response[key].status=''+data.response[key].status+'';
			            }
			        }
			  	if(data.response[key].status==null){
					htmlist+='
'
					+''
					+data.response[key].start_date+'

'
					+data.response[key].start_time+'
'
					+data.response[key].event
			        +((data.response[key].opponent)?' vs. '+data.response[key].opponent.replace('Away vs.',''):'')
			        +((data.response[key].location)?' 
@ '+data.response[key].location:'')
			        +'';
			   	}else{
			    	htmlist+='
'
			        +''
			        +data.response[key].start_date+'

'
			        +data.response[key].start_time+'
'
			        +data.response[key].event
			        +((data.response[key].opponent)?' vs. '+data.response[key].opponent.replace('Away vs.',''):'')
			        +((data.response[key].location)?'
 @ '+data.response[key].location:'')
			        +' '+data.response[key].status+'';
			    }
			}
			$("#events_list ul").html(htmlist);
		}else{
			num++;
			if(num%5==0){
				var htmlist='Calendar Service Response '+ data.code;
				htmlist+=' Reload';
				$("#events_list ul").html(htmlist);
				$("#reloading_event_info").click(function(){
				$("#events_list ul").html(' Loading Events...');
				num++;
				load_events_list(num);
				return false;
				});
			}else{
				setTimeout('load_events_list('+num+')',1500);
			}
		}
	});
}

img.script:
// Bug 76422

		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker16 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker16 a.ui-datepicker-prev').click();
	    		handlePrevClick16();
	    	}
	    });
	    $('#datepicker16 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker16 a.ui-datepicker-next').click();
	    		handleNextClick16();
	    	}
	    });


		function handlePrevClick16(){
			setTimeout(function () {
				triggerClick16();
			    $('#datepicker16').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick16(){
			setTimeout(function () {
				triggerClick16();
			    $('#datepicker16').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick16(){
			prev = $('#datepicker16').find('.ui-datepicker-prev');
			next = $('#datepicker16').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick16);
			$(prev).on('click', handlePrevClick16);
		}

		};



$(document).ready(function() {
var server = 'no';
if (server=='yes') {
	var firstLoad16 = true;
}else{
	var firstLoad16 = ($.fn.jquery.split('.')[1])<6?true:false;
}
var doe=[];
var load_doe16=function(y,m,callback){
  if(y.length!=4||m.length!=2) return;
  $.getJSON(window.location.protocol+'//'+window.location.host+'/calendar/doe/'+y+'/'+m, callback);
}
var proc_doe16=function(y,m,callback){
  var s=y+'-'+m;
  if(doe.indexOf(s)==-1){
    doe.push(s);
    load_doe16(y,m, function(data){
      if(data.code==200){
        for(key in data.response){
          var s=data.response[key].game_date;
          doe.indexOf(s)==-1&&doe.push(s);
        }
      }
      callback();
    });
  }else callback();
}

var cal_init16=function(){
  proc_doe16('2020','04',function(){
    $('#cal_loadimg16').hide();
    jQuery('#datepicker16').datepicker({
      prevText: ' ',
      nextText: ' ',
      onSelect: function(dateText, inst) {
//       	inst.inline = false;
        if (!firstLoad16) {
          var calendar_url = 'http://www.mnriverconference.org/public/genie/28/school/5/';
          if(calendar_url.length<3)return;
          var selected_date = new Date(dateText);
          var additional_url = 'date/' +selected_date.getFullYear()+'-'+ (parseInt(selected_date.getMonth()) + 1) +'-' + selected_date.getDate() + '/view/day/';
calendar_url += additional_url;
          window.open(calendar_url,'_blank');
        }
        firstLoad16 = false;
      },
      onChangeMonthYear: function(y,m,obj){
        y+='';m=(m<10?'0':'')+m;
        doe.indexOf(y+'-'+m)==-1&&proc_doe16(y,m,function(){
          console.log(doe);
          jQuery('#datepicker16').datepicker();
        });
        setTimeout(function () {

		$('#datepicker16 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker16 a.ui-datepicker-prev').click();
	    		handlePrevClick16();
	    	}
	    });
	    $('#datepicker16 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker16 a.ui-datepicker-next').click();
	    		handleNextClick16();
	    	}
	    });


		function handlePrevClick16(){
			setTimeout(function () {
				triggerClick16();
			    $('#datepicker16').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick16(){
			setTimeout(function () {
				triggerClick16();
			    $('#datepicker16').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick16(){
			prev = $('#datepicker16').find('.ui-datepicker-prev');
			next = $('#datepicker16').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick16);
			$(prev).on('click', handlePrevClick16);
		}

        }, 50);
      },
      beforeShowDay : function(date) {
        var y=date.getFullYear()+'',m=date.getMonth()+1,d=date.getDate();
        m=(m<10?'0':'')+m;d=(d<10?'0':'')+d;
        return [ true, doe.indexOf(y+'-'+m+'-'+d)>0?'call-bold':'', '' ];
      },
    });
  });
}
