		window.addEventListener('load', myInit, true);

		function myInit(){

		$('#datepicker2 a.ui-datepicker-prev').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker2 a.ui-datepicker-prev').click();
	    		handlePrevClick2();
	    	}
	    });
	    $('#datepicker2 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker2 a.ui-datepicker-next').click();
	    		handleNextClick2();
	    	}
	    });


		function handlePrevClick2(){
			setTimeout(function () {
				triggerClick2();
			    $('#datepicker2').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick2(){
			setTimeout(function () {
				triggerClick2();
			    $('#datepicker2').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick2(){
			prev = $('#datepicker2').find('.ui-datepicker-prev');
			next = $('#datepicker2').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick2);
			$(prev).on('click', handlePrevClick2);
		}

		};



