	    		$('#datepicker3 a.ui-datepicker-prev').click();
	    		handlePrevClick3();
	    	}
	    });
	    $('#datepicker3 a.ui-datepicker-next').keydown(function(e){
	    	if(e.which == 13){ // enter key
	    		e.preventDefault();
	    		$('#datepicker3 a.ui-datepicker-next').click();
	    		handleNextClick3();
	    	}
	    });


		function handlePrevClick3(){
			setTimeout(function () {
				triggerClick3();
			    $('#datepicker3').find('.ui-datepicker-prev').focus();
			}, 0);
		}
		function handleNextClick3(){
			setTimeout(function () {
				triggerClick3();
			    $('#datepicker3').find('.ui-datepicker-next').focus();
			}, 0);
		}
		function triggerClick3(){
			prev = $('#datepicker3').find('.ui-datepicker-prev');
			next = $('#datepicker3').find('.ui-datepicker-next');
			prev.attr('tabindex','0');
			next.attr('tabindex','0');

			$(next).on('click', handleNextClick3);
			$(prev).on('click', handlePrevClick3);
		}

		};



