    ],
    "scripts": [
        "\/ui\/overrides\/owl-carousel\/owl.carousel.min.js"
    ],
    "plugin": "OwlSlider",
    "pluginId": "cms-widget-22575ce7-e3e8-41a6-8e29-2a03581cdd4a",
    "pluginOptions": {
        "transition": "fade",
        "transitionSpeed": 10,
        "navigation": true,
