(function (window, document, $, undefined) {
    $(function () {
        $('#cms-widget-17d5cd31-f1a9-40b1-9362-f86c7f2dd617').on('click', '.meta-event-attachment-link', function (e) {
            var $target = $(e.currentTarget);
            var p = $.ajax({
                url: $target.attr('data-ajax'),
            });
            p.done(function (resp) {
                $(resp)
                    .appendTo($('body'))
                    .modal({
                        show: false
                    })
                    .on('show.bs.modal', function () {
                        $(this).css('display', 'block');
                        var $d = $(this).find('.modal-dialog'),
                            o = ($(window).height() - $d.height()) / 2,
                            bm = parseInt($d.css('marginBottom'), 10);
                        o = Math.max(o, bm);
                        $d.css('margin-top', o);
                    })
                    .on('hidden.bs.modal', function () {
                        $(this).remove();
                    })
                    .modal('show');
            });
            e.preventDefault();
        });
    });
})(window, document, jQuery);

