;(function($) {
  'use strict';
  $(function() {
    // Wait until all of the images in the features are loaded
    $('.socs-feature-img').imagesLoaded(function() {
      $('.socs-feature.two').each(function() {
        var $this = $(this);

        // Add a border on the side of whichever box is the tallest - .two or .three
        if ($this.height() >= $this.next().height()) {
          $this.addClass('bordered');
        } else {
          $this.next().addClass('bordered');
        }
      });

      // If the last feature is the left-side of the "split" - undo the split
      var $lastFeature = $('.socs-feature:last');
      if ($lastFeature.hasClass('two')) {
        $lastFeature.css({
          'float': 'none',
          'padding-right': '0',
          'width': 'auto'
        }).removeClass('bordered');
      }
    });

  });
})(jQuery);

;(function($) {
  var href = window.location.href;
  if (href.indexOf('?') === -1) {
    href += '?template=m';
  } else {
    href += '&template=m';
  }
  $('#copyRight .mobileSite').attr('href', href).click(function() {
    // Reset the session cookie to redirect to mobile.
    $.cookie('socs_MobileSwitch', 'y', { path: '/' });
  });
})(jQuery);
