listenButton1.onclick = function(){
    if(responsiveVoice.isPlaying()){
        responsiveVoice.cancel();
    }else{
        responsiveVoice.speak("NEWS UPDATE: The governor has announced that schools will remain closed through the rest of the school year. Summer school is still to be determined. Information from the Nurse Vaping Resources Click here to view a slideshow on how to access google classroom! Want to be notified of school related updates via text messages?  Simply email tech@newburg.k12.mo.us with your student\'s name, grade, and what phone numbers you wish to be contacted on.", "UK English Female");
    }
};

body.script:
	function determineEnglish() {
		var body = document.body;
		var textContent = body.textContent || body.innerText;
		var textContent = textContent.replace(/\n/g," ");
		var textContent = textContent.replace(/\r/g," ");
		var textContent = textContent.replace(/\t/g," ");
		var textContent = textContent.replace(/ /g,"");
		var textLeft = textContent.replace(/\W+/g,"");
		var oldc = textContent.length;
		var newc = textLeft.length;
		var ratio = newc/oldc;
		if(ratio>.8) {
			return "english";
		} else {
			return "other";
		}
	}



    window.accPlayerStatus = "uninit";

    if(responsiveVoice.voiceSupport() && determineEnglish()=="english") {
        var obj = document.getElementById("btnAccPlay");
        obj.style.cursor="pointer";  
    } else {
        document.getElementById("real-accessability-player").style.display="none";
    }

    if(navigator.userAgent.indexOf("OPR")!=-1) {
        document.getElementById("real-accessability-player").style.display="none";
    } 

    function accPlayer(btnType) {

        // TURN ALL TO GRAY

        var playObj  = document.getElementById("btnAccPlay");
        var pauseObj = document.getElementById("btnAccPause");
        var stopObj  = document.getElementById("btnAccStop");

        if(btnType=="play") {

            if(window.accPlayerStatus=="uninit") {

                // CHANGE STATUS TO PLAYING
                window.accPlayerStatus = "playing";

                // LOAD THE PAGE CONTENT ALONE
                var u = location.href;
                var s = document.createElement("script");
                s.setAttribute("type","text/javascript")
                s.src = "//508fi.org/js/speech.php?u="+encodeURIComponent(u);
                document.getElementsByTagName("head")[0].appendChild(s);

                // ASSIGN CORRECT COLORS
                playObj.src  = playObj.src.replace("blue","gray");
                stopObj.src  = stopObj.src.replace("gray","red");
                pauseObj.src = pauseObj.src.replace("gray","blue");

            } else if(window.accPlayerStatus=="playing") {

            } else if(window.accPlayerStatus=="paused") {

                // CHANGE STATUS TO PLAYING
                window.accPlayerStatus = "playing";

                // RESUME PLAYING
                responsiveVoice.resume();

                // ASSIGN CORRECT COLORS
                 playObj.src  = playObj.src.replace("blue","gray");
                 stopObj.src  = stopObj.src.replace("gray","red");
                 pauseObj.src = pauseObj.src.replace("gray","blue");

            } else if(window.accPlayerStatus=="stopped") {

                // CHANGE STATUS TO PLAYING
                window.accPlayerStatus = "playing";

                // LOAD THE PAGE CONTENT ALONE
                var u = location.href;
                var s = document.createElement("script");
                s.setAttribute("type","text/javascript")
                s.src = "//508fi.org/js/speech.php?u="+encodeURIComponent(u);
                document.getElementsByTagName("head")[0].appendChild(s);

                // ASSIGN CORRECT COLORS
                 playObj.src  = playObj.src.replace("blue","gray");
                 stopObj.src  = stopObj.src.replace("gray","red");
                 pauseObj.src = pauseObj.src.replace("gray","blue");

            } else {

            }

        } else if(btnType=="pause") {
            if(window.accPlayerStatus=="uninit") {

            } else if(window.accPlayerStatus=="playing") {

                // CHANGE STATUS TO PLAYING
                window.accPlayerStatus = "paused";

                // PAUSE READING
                responsiveVoice.pause();

                // ASSIGN CORRECT COLORS
                 playObj.src  = playObj.src.replace("gray","blue");
                 stopObj.src  = stopObj.src.replace("gray","red");
                 pauseObj.src = pauseObj.src.replace("blue","gray");

            } else if(window.accPlayerStatus=="paused") {

            } else if(window.accPlayerStatus=="stopped") {

            } else {

            }

        } else if(btnType=="stop") {

            if(window.accPlayerStatus=="uninit") {

            } else if(window.accPlayerStatus=="playing") {

                // STOP READING
                responsiveVoice.cancel();

                // ASSIGN CORRECT COLORS
                 playObj.src  = playObj.src.replace("gray","blue"); 
                 stopObj.src  = stopObj.src.replace("red","gray");
                 pauseObj.src = pauseObj.src.replace("blue","gray");

            } else if(window.accPlayerStatus=="paused") {

                // STOP READING
                responsiveVoice.cancel();

                // ASSIGN CORRECT COLORS
                 playObj.src  = playObj.src.replace("gray","blue"); 
                 stopObj.src  = stopObj.src.replace("red","gray");
                 pauseObj.src = pauseObj.src.replace("blue","gray");

            } else if(window.accPlayerStatus=="stopped") {

            } else {}

        } else {}

    }
