$(".group865984").colorbox({rel:'group865984', maxWidth: '80%'});
    $( window ).resize( function(){
        if( $('#cboxOverlay' ).css( 'visibility' ) ){
        $(".group865984").colorbox.resize({ innerWidth: '80%', scalePhotos: 'true'});
    }
});
var $gallery865984 = $("a[rel=group865984]").colorbox({maxWidth: "80%"});
$("a#openGallery865984").click(function(e){
    e.preventDefault();
    $gallery865984.eq(0).click();
});

