			function rotateSlideshow(position) {
				if (position > 0) {
					for (var i = 0; i < position; i++) {
						$('.slideshow-image:first').appendTo("#slideshow .slides");
					}
					redrawSlideshow();
					if (slideshowInterval == null) {
