				$('.slideshow-image:first').fadeTo(timeout, .1, function () {
					$(this).appendTo("#slideshow .slides");
					$('.slideshow-image:first').css({ opacity: .1 }).fadeTo(timeout, 1);

					$(this).fadeTo(timeout, 1);
					redrawSlideshow();
					$('#slideshow').fadeTo(timeout, 1);
				});
			}

			var to = null;
			$(window).resize(function () {
